let buttons=document.querySelectorAll(".drum");function makeSound(e){let s;switch(buttonAnimation(e),e){case"w":s=new Audio("./sounds/tom-1.mp3"),s.play();break;case"a":s=new Audio("./sounds/tom-2.mp3"),s.play();break;case"s":s=new Audio("./sounds/tom-3.mp3"),s.play();break;case"d":s=new Audio("./sounds/tom-4.mp3"),s.play();break;case"j":s=new Audio("./sounds/snare.mp3"),s.play();break;case"k":s=new Audio("./sounds/crash.mp3"),s.play();break;case"l":s=new Audio("./sounds/kick-bass.mp3"),s.play();break;default:break}}function buttonAnimation(e){let s=document.querySelector("."+e);s.classList.add("pressed"),setTimeout((()=>{s.classList.remove("pressed")}),100)}buttons.forEach((e=>{e.addEventListener("click",(()=>{makeSound(e.innerHTML)}))})),document.addEventListener("keydown",(e=>{makeSound(e.key)}));