let buttons=document.querySelectorAll(".drum");buttons.forEach((e=>{e.addEventListener("click",(()=>{let s;switch(e.innerHTML){case"w":s=new Audio("./sounds/tom-1.mp3"),s.play();break;case"a":s=new Audio("./sounds/tom-2.mp3"),s.play();break;case"s":s=new Audio("./sounds/tom-3.mp3"),s.play();break;case"d":s=new Audio("./sounds/tom-4.mp3"),s.play();break;case"j":s=new Audio("./sounds/snare.mp3"),s.play();break;case"k":s=new Audio("./sounds/crash.mp3"),s.play();break;case"l":s=new Audio("./sounds/kick-bass.mp3"),s.play();break;default:break}}))}));